import React, { useEffect, useState, useContext } from 'react';
import { CurrentUserContext } from './UserInfo';
import Popup from 'reactjs-popup';

function Login() {
    const [currentUser, setCurrentUser ] = useContext(CurrentUserContext)
    const [username, setUsername] = useState("")
    const [users, setUsers] = useState([])

    useEffect(() => {
        fetch("http://localhost:9292/user_profiles")
          .then((r) => r.json())
          .then((resp) => setUsers(resp));
      }, []);

    function handleUsername(e){
        setUsername(e.target.value)
    }

    const test = users.find(user =>{
        return username === user.user_name
    })
    console.log(test)

    function handleUserSubmit(e){
        e.preventDefault()
        if (test === undefined){
            setCurrentUser(username)
            alert("No user found. Please create an account.")
        } else{
            setCurrentUser("")
        }
    }

    return ( 
        <p className="login">
          {currentUser}
          <Popup trigger=
          {<button>{(currentUser === "") ? "Login" : "Log out"}</button>}
          modal nested>
            {close => (
              <>
              <form onSubmit={(e) => {
                handleUserSubmit(e)
                close()
              }}>
                <input type="text" value={username} onChange={handleUsername}/>
                <input type="submit" value="Login" />
                <input type="submit" value="Create User" />
              </form>
              </>
            )}
          </Popup>
        </p>
    )

}

export default Login;
