    const navigate = useNavigate()

    console.log(currentUser)

    useEffect(() => {
        fetch("http://localhost:9292/user_profiles")
          .then((r) => r.json())
          .then((resp) => {
            setUsers(resp)
            setCurrentUser(resp[1])})
      }, []);

    return ( 
        <p className="login">
            {(currentUser.length === 0) ? "Please login" : `${currentUser.user_name}`}
