import React, { useState } from 'react';
import Popup from 'reactjs-popup';

function RecordEdit({ record, exercise, categories }){
    const [metric, setMetric] = useState('')
    const [recordDate, setRecordDate] = useState('')
    const [recordCategory, setRecordCategory] = useState('')
    const [metricDisplay, setMetricDisplay] = useState('')

    function handleMetricDisplay(){
        if (exercise.for_weight){
            return(
                <>
                <label>Metric</label>
                <input type="text" value={metric} onChange={handleMetricChange} placeholder={record.metric}/>
                <label>lbs</label>
                </>
            )
        } else if (exercise.for_reps){
            return(
                <>
                <label>Metric</label>
                <input type="text" value={metric} onChange={handleMetricChange} placeholder={record.metric}/>
                <label>reps</label>
                </>
            ) 
        } else if (exercise.for_time){
            const minutes = Math.floor(record.metric / 60)
            const seconds = (record.metric % 60)
            return(
                <>
                <label>Metric</label>
                <input type="text" value={metric} onChange={handleMetricChange} placeholder={minutes}/>
                <label>mins</label>
                <input type="text" value={metric} onChange={handleMetricChange} placeholder={seconds}/>
                <label>secs</label>
                </>
            ) 
        }
    }

    function handleMetricChange(e){
        e.preventDefault()
        setMetric(e.target.value)
    }

    function handleRecordDateChange(e){
        e.preventDefault()
        setRecordDate(e.target.value)
    }

    function handleRecordCategoryChange(e){
        e.preventDefault()
        setRecordCategory(e.target.value)
    }

    function categoriesButtons(){
        return(
            categories.map(category =>{
                return(
                    <button>{category.category}</button>
                )
            })
        )
    }

    function handleCategoryDisplay(){
        const categoryArray = categories.map(category => {
            return category.category
        })
        return (
            <>
            <input type="text" value={recordCategory} onChange={handleRecordCategoryChange} placeholder="--add new category--"/><br></br>
            {categoriesButtons()}
            </>
        )
    }

    function handleRecordEditSubmit(){
    }

    return(
        <td>
            <Popup trigger=
                {<button>edit</button>}
            modal nested>
            {close => (
              <>
              <form className="record-edit" onSubmit={(e) => {
                handleRecordEditSubmit(e)
                close()
              }}>
                <legend>{exercise.exercise}</legend>
                {handleMetricDisplay()}<br></br>
                <label>Date</label>
                <input type="text" value={recordDate} onChange={handleRecordDateChange} placeholder={record.date_created.slice(0,10)}/><br></br>
                {handleCategoryDisplay()}<br></br>
                <input type="submit" value="Update" />
              </form>
              </>
            )}
          </Popup>
        </td>
    )
}

export default RecordEdit;

