import React, { useState } from 'react';

function Filter ({ onExerciseFilter }) {
    const [exerciseFilter, setExerciseFilter] = useState('')
    const [categoryFilter, setCategoryFilter] = useState('')

    function handleExerciseFilter(e){
        onExerciseFilter(e.target.value)
    }

    function handleCategoryFilter(e){
        e.preventDefault()
        setCategoryFilter(e.target.value)
    }
    return(
        <form className="filter-form">
            <label>Filter:</label>
            <label>Exercise</label>
            <input type="text" value={exerciseFilter} onChange={handleExerciseFilter} placeholder="ex. Bench Press"/>
            <label>Category</label>
            <input type="text" value={categoryFilter} onChange={handleCategoryFilter} placeholder="ex. Upper body"/>
        </form>
    )
}

export default Filter;

