import React, { useState } from 'react';

function CategoryAdd({ categories }){
    const [categoriesToAdd, setCategoriesToAdd] = useState([])

    function handleCategoriesToAdd(e){
        setCategoriesToAdd([...categoriesToAdd, e.target.value])

    }
    console.log(categoriesToAdd)

    return(
        <select onChange={handleCategoriesToAdd}>
            {categories.map(cat =>(
                <option key={cat.id} value={cat.category}>{cat.category}</option>
            ))}
        </select>
    )
}

export default CategoryAdd;
