import React, { useState } from 'react';
import Popup from 'reactjs-popup';

function RecordEdit({ record, exercise, categories }){
    const [metric, setMetric] = useState('')

    function metricDisplay(){
        if (exercise.for_reps){
            return(
                `${record.metric} reps`
            )
        } else if (exercise.for_weight){
            return(
                `${record.metric} lbs`
            )
        } else if (exercise.for_time){
            const minutes = Math.floor(record.metric / 60)
            const seconds = (record.metric % 60)
            return(
                `${minutes} mins, ${seconds} secs`
            )
        }
    }

    function handleMetricChange(e){
        e.preventDefault()
        setMetric(e.target.value)
    }

    function handleRecordEditSubmit(){
        console.log("test")
    }

    return(
        <td>
            <Popup trigger=
                {<button>edit</button>}
            modal nested>
            {close => (
              <>
              <form onSubmit={(e) => {
                handleRecordEditSubmit(e)
                close()
              }}>
                <legend>{exercise.exercise}</legend>
                <label for="metric">Metric</label>
                <input type="text" name="metric"id="metric" value={metric} onChange={handleMetricChange} placeholder={metricDisplay}/>
                <input type="submit" value="Update" />
              </form>
              </>
            )}
          </Popup>
        </td>
    )
}

export default RecordEdit;

