import React, { useEffect, useState } from 'react';
import Exercise from './Exercise';
import Popup from 'reactjs-popup';

function RecordAdd(){
    const [exercises, setExercises] = useState([])
    const [exerciseSelection, setExerciseSelection] = useState('');
    const [categories, setCategories] = useState([])

    useEffect(() => {
        fetch("http://localhost:9292/exercises")
          .then((r) => r.json())
          .then((exercises) => setExercises(exercises));
        fetch(`http://localhost:9292/categories`)
          .then((r) => r.json())
          .then((resp) => setCategories(resp));
      }, []);

    function categoryList(){

    }

    function timeMetric() {
        return(
            <>
                <input type="number" placeholder="minutes..." /><span>lbs</span><br></br>
                <input type="number" placeholder="seconds..." /><span>lbs</span><br></br>
            </>
        )}

    function handleAddRecord(){
        let metric
        if (exerciseSelection !== ""){
            const exerciseToAdd = exercises.filter(e =>{
                return (e.exercise === exerciseSelection)
            })
            console.log(exerciseToAdd)
            if (!exerciseToAdd.for_time){
                return(
                    <form>
                        <legend>{exerciseSelection}: </legend>
                        <label>Metric</label>
                        <input type="text" placeholder={(exerciseToAdd.for_weight) ? "200..." : "15..."} /><span>{(exerciseToAdd.for_weight) ? "lbs" : "reps"}</span><br></br>
                        <label>Date</label>
                        <input type="date" /><br></br>
                        <label>Category</label>
                        <select multiple>
                            {categories.map(cat =>(
                                <option key={cat.id} value={cat.category}>{cat.category}</option>
                            ))}
                        </select>
                    </form>
                )
            }
        }
    }

    function handleRecordAddSubmit(e){
        e.preventDefault()
    }

    return(
            <Popup trigger=
                {<button>Add Record</button>}
            modal nested>
            {close => (
              <div className="record-add">
              <form onSubmit={(e) => {
                handleRecordAddSubmit(e)
                close()
              }}>
                <legend>Add Record</legend>
                <select value={exerciseSelection} onChange={e => setExerciseSelection(e.target.value)}>
                    <option>--Choose Exercise--</option>
                    {exercises.map((exercise) => (
                            <Exercise
                                key={exercise.id}
                                exercise={exercise}
                            />
                        ))}
                    <option>--Create New--</option>
                </select>
                <input type="submit" value="Add" />
              </form>
              {handleAddRecord()}
              </div>
            )}
          </Popup>
    )

}

export default RecordAdd;
