import React, { useEffect, useState, useContext } from 'react';
import { CurrentUserContext } from './UserInfo';
import { useNavigate } from "react-router-dom"


function Login({ onLogin }) {
    const [currentUser, setCurrentUser ] = useContext(CurrentUserContext)
    const [users, setUsers] = useState([])
    const navigate = useNavigate()
    const [isLoggedIn, setIsLoggedIn] = useState(false)

    useEffect(() => {
        fetch("http://localhost:9292/user_profiles")
          .then((r) => r.json())
          .then((resp) => {
            setUsers(resp)
            setCurrentUser(resp[1])})
            setIsLoggedIn(!isLoggedIn)
            onLogin(isLoggedIn)
      }, []);

    function handleNewLogin(){
        navigate("/login")
    }

    return ( 
        <p className="login">
            {(currentUser.length === 0) ? "Please login" : `${currentUser.user_name}`}
            <span>
            <button onClick={handleNewLogin}>{(currentUser.length === 0) ? "Login" : "Log out"}</button>
          </span>
        </p>
    )

}

export default Login;
